display:flex;
height:25px;
margin-left:14px;
border-radius:3px;
margin-block-start:3px;

`;
export const Label = styled.label`
font-size:20px;

`;
export const Img = styled.img`
height:300px;
width:300px;
`;
export const Bt = styled.button`
